mapper.select:
select
  id, p_id, ctr_id, company_id, contract_name, contract_amount, settle_type, agree_amount, agree_upper, penalty, withhold, settle_comment, handler_id, apply_date, attach_ids, approve_status, update_user, update_date
from engineer.apply_progress_payment


        and id = #{id}


        and p_id = #{pId}


        and ctr_id = #{ctrId}


        and company_id = #{companyId}


        and contract_name = #{contractName}


        and contract_amount = #{contractAmount}


        and settle_type = #{settleType}


        and agree_amount = #{agreeAmount}


        and agree_upper = #{agreeUpper}


        and penalty = #{penalty}


        and withhold = #{withhold}


        and settle_comment = #{settleComment}


        and handler_id = #{handlerId}


        and apply_date = #{applyDate}


        and attach_ids = #{attachIds}


        and approve_status = #{approveStatus}


        and update_user = #{updateUser}


        and update_date = #{updateDate}
